import React, { useState, useEffect } from 'react';
import axios from 'axios';

function GestionUsuarios({ usuario }) {
  // Estados para datos
  const [usuarios, setUsuarios] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [notificacion, setNotificacion] = useState({ tipo: '', mensaje: '' });
  
  // Estados para formulario
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [editandoUsuario, setEditandoUsuario] = useState(null);
  const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    password: '',
    rut: '',
    rol: 'guardia',
    telefono: '',
    direccion: ''
  });

  // Estados para filtros
  const [filtroRol, setFiltroRol] = useState('');
  const [filtroNombre, setFiltroNombre] = useState('');
  const [usuariosFiltrados, setUsuariosFiltrados] = useState([]);

  // Cargar usuarios
  const cargarUsuarios = async () => {
    setCargando(true);
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    
    try {
      const response = await axios.get('http://localhost:5000/usuarios/todos', { headers });
      setUsuarios(response.data);
      setUsuariosFiltrados(response.data);
    } catch (error) {
      setNotificacion({ tipo: 'danger', mensaje: 'Error al cargar usuarios: ' + (error.response?.data?.error || error.message) });
    } finally {
      setCargando(false);
    }
  };

  useEffect(() => {
    cargarUsuarios();
  }, [usuario]);

  // Filtrar usuarios
  const filtrarUsuarios = () => {
    let filtrados = usuarios;

    if (filtroRol) {
      filtrados = filtrados.filter(user => user.rol === filtroRol);
    }

    if (filtroNombre) {
      filtrados = filtrados.filter(user => 
        user.nombre && user.nombre.toLowerCase().includes(filtroNombre.toLowerCase())
      );
    }

    setUsuariosFiltrados(filtrados);
  };

  useEffect(() => {
    filtrarUsuarios();
  }, [filtroRol, filtroNombre, usuarios]);

  // Limpiar formulario
  const limpiarFormulario = () => {
    setFormData({
      nombre: '',
      email: '',
      password: '',
      rut: '',
      rol: 'guardia',
      telefono: '',
      direccion: ''
    });
    setEditandoUsuario(null);
  };

  // Abrir formulario para crear
  const abrirFormularioCrear = () => {
    limpiarFormulario();
    setMostrarFormulario(true);
  };

  // Abrir formulario para editar
  const abrirFormularioEditar = (usuario) => {
    setFormData({
      nombre: usuario.nombre || '',
      email: usuario.email || '',
      password: '', // No mostrar password actual
      rut: usuario.rut || '',
      rol: usuario.rol || 'guardia',
      telefono: usuario.telefono || '',
      direccion: usuario.direccion || ''
    });
    setEditandoUsuario(usuario);
    setMostrarFormulario(true);
  };

  // Cerrar formulario
  const cerrarFormulario = () => {
    setMostrarFormulario(false);
    limpiarFormulario();
  };

  // Manejar cambios en el formulario
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Crear usuario
  const handleSubmit = async (e) => {
    e.preventDefault();
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    
    try {
      if (editandoUsuario) {
        await axios.put(`http://localhost:5000/usuarios/${editandoUsuario._id}`, formData, { headers });
        setNotificacion({ tipo: 'success', mensaje: 'Usuario actualizado correctamente' });
      } else {
        await axios.post('http://localhost:5000/usuarios', formData, { headers });
        setNotificacion({ tipo: 'success', mensaje: 'Usuario creado correctamente' });
      }
      
      setFormData({ nombre: '', email: '', password: '', rut: '', rol: 'guardia' });
      setEditandoUsuario(null);
      cargarUsuarios();
    } catch (error) {
      setNotificacion({ 
        tipo: 'danger', 
        mensaje: error.response?.data?.error || 'Error al procesar usuario' 
      });
    }
  };

  // Eliminar usuario
  const eliminarUsuario = async (usuarioId, nombreUsuario) => {
    if (!window.confirm(`¿Estás seguro de que quieres eliminar al usuario "${nombreUsuario}"?`)) {
      return;
    }
    
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    
    try {
      const response = await axios.delete(`http://localhost:5000/usuarios/${usuarioId}`, { headers });
      setNotificacion({ tipo: 'success', mensaje: response.data.mensaje });
      cargarUsuarios();
    } catch (error) {
      setNotificacion({ 
        tipo: 'danger', 
        mensaje: 'Error al eliminar usuario: ' + (error.response?.data?.error || error.message) 
      });
    }
  };

  // Limpiar notificación automática
  useEffect(() => {
    if (notificacion.mensaje) {
      const timer = setTimeout(() => setNotificacion({ tipo: '', mensaje: '' }), 5000);
      return () => clearTimeout(timer);
    }
  }, [notificacion]);

  return (
    <div className="card mt-4">
      <div className="card-header bg-dark text-white d-flex justify-content-between align-items-center">
        <h5 className="mb-0">👥 Gestión de Usuarios</h5>
        <div>
          <button className="btn btn-outline-light btn-sm me-2" onClick={cargarUsuarios} disabled={cargando}>
            <i className="fas fa-sync-alt"></i> Actualizar
          </button>
          <button className="btn btn-success btn-sm" onClick={abrirFormularioCrear}>
            <i className="fas fa-plus"></i> Nuevo Usuario
          </button>
        </div>
      </div>
      
      <div className="card-body">
        {/* Notificación */}
        {notificacion.mensaje && (
          <div className={`alert alert-${notificacion.tipo} mb-3`} role="alert">
            {notificacion.mensaje}
          </div>
        )}

        {/* Filtros */}
        <div className="row mb-3">
          <div className="col-md-4">
            <label className="form-label">Filtrar por rol:</label>
            <select 
              className="form-select" 
              value={filtroRol} 
              onChange={e => setFiltroRol(e.target.value)}
            >
              <option value="">Todos los roles</option>
              <option value="supervisor">Supervisor</option>
              <option value="guardia">Guardia</option>
            </select>
          </div>
          <div className="col-md-4">
            <label className="form-label">Filtrar por nombre:</label>
            <input
              type="text"
              className="form-control"
              placeholder="Buscar por nombre..."
              value={filtroNombre}
              onChange={e => setFiltroNombre(e.target.value)}
            />
          </div>
          <div className="col-md-4 d-flex align-items-end">
            <div className="text-muted">
              <small>Mostrando {usuariosFiltrados.length} de {usuarios.length} usuarios</small>
            </div>
          </div>
        </div>

        {/* Lista de usuarios */}
        {cargando ? (
          <div className="text-center py-4">
            <div className="spinner-border text-dark" role="status">
              <span className="visually-hidden">Cargando...</span>
            </div>
          </div>
        ) : usuariosFiltrados.length === 0 ? (
          <div className="text-center text-muted py-4">
            <i className="fas fa-users fa-3x mb-3"></i>
            <p>No hay usuarios que coincidan con los filtros</p>
          </div>
        ) : (
          <div className="table-responsive">
            <table className="table table-striped table-hover">
              <thead className="table-dark">
                <tr>
                  <th>#</th>
                  <th>Nombre</th>
                  <th>Email</th>
                  <th>RUT</th>
                  <th>Rol</th>
                  <th>Teléfono</th>
                  <th>Estado</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {usuariosFiltrados.map((user, index) => (
                  <tr key={user._id}>
                    <td>{index + 1}</td>
                    <td>
                      <i className="fas fa-user me-2"></i>
                      {user.nombre}
                    </td>
                    <td>{user.email}</td>
                    <td>{user.rut}</td>
                    <td>
                      <span className={`badge ${user.rol === 'supervisor' ? 'bg-warning' : 'bg-primary'}`}>
                        {user.rol}
                      </span>
                    </td>
                    <td>{user.telefono || '-'}</td>
                    <td>
                      <span className={`badge ${user.activo ? 'bg-success' : 'bg-danger'}`}>
                        {user.activo ? 'Activo' : 'Inactivo'}
                      </span>
                    </td>
                    <td>
                      <div className="btn-group btn-group-sm">
                        <button 
                          className="btn btn-outline-primary"
                          onClick={() => abrirFormularioEditar(user)}
                          title="Editar"
                        >
                          <i className="fas fa-edit"></i>
                        </button>
                        {user.rol !== 'gerente' && (
                          <button 
                            className="btn btn-outline-danger"
                            onClick={() => eliminarUsuario(user._id, user.nombre)}
                            title="Eliminar"
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Modal de formulario */}
      {mostrarFormulario && (
        <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {editandoUsuario ? 'Editar Usuario' : 'Crear Nuevo Usuario'}
                </h5>
                <button type="button" className="btn-close" onClick={cerrarFormulario}></button>
              </div>
              <form onSubmit={handleSubmit}>
                <div className="modal-body">
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Nombre completo *</label>
                      <input
                        type="text"
                        className="form-control"
                        name="nombre"
                        value={formData.nombre}
                        onChange={handleInputChange}
                        required
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Email *</label>
                      <input
                        type="email"
                        className="form-control"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        required
                      />
                    </div>
                  </div>
                  
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">
                        {editandoUsuario ? 'Nueva contraseña (dejar vacío para no cambiar)' : 'Contraseña *'}
                      </label>
                      <input
                        type="password"
                        className="form-control"
                        name="password"
                        value={formData.password}
                        onChange={handleInputChange}
                        required={!editandoUsuario}
                      />
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">RUT *</label>
                      <input
                        type="text"
                        className="form-control"
                        name="rut"
                        value={formData.rut}
                        onChange={handleInputChange}
                        placeholder="XX.XXX.XXX-X"
                        required
                      />
                    </div>
                  </div>
                  
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Rol *</label>
                      <select
                        className="form-select"
                        name="rol"
                        value={formData.rol}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="guardia">Guardia</option>
                        <option value="supervisor">Supervisor</option>
                      </select>
                    </div>
                    <div className="col-md-6 mb-3">
                      <label className="form-label">Teléfono</label>
                      <input
                        type="tel"
                        className="form-control"
                        name="telefono"
                        value={formData.telefono}
                        onChange={handleInputChange}
                      />
                    </div>
                  </div>
                  
                  <div className="mb-3">
                    <label className="form-label">Dirección</label>
                    <textarea
                      className="form-control"
                      name="direccion"
                      value={formData.direccion}
                      onChange={handleInputChange}
                      rows="2"
                    ></textarea>
                  </div>
                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-secondary" onClick={cerrarFormulario}>
                    Cancelar
                  </button>
                  <button type="submit" className="btn btn-primary">
                    {editandoUsuario ? 'Actualizar' : 'Crear'} Usuario
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Overlay del modal */}
      {mostrarFormulario && (
        <div className="modal-backdrop fade show"></div>
      )}
    </div>
  );
}

export default GestionUsuarios;
