import React, { useEffect, useState } from 'react';
import axios from 'axios';

function VistaGerente({ usuario }) {
  const [reportes, setReportes] = useState([]);
  const [tareas, setTareas] = useState([]);
  const [turnos, setTurnos] = useState([]);

useEffect(() => {
  const headers = { Rol: usuario.rol };

  axios.get('http://localhost:5000/reportes', { headers })
    .then(res => setReportes(res.data))
    .catch(err => console.error("Error al obtener reportes:", err));

  axios.get('http://localhost:5000/tareas', { headers })
    .then(res => setTareas(res.data))
    .catch(err => console.error("Error al obtener tareas:", err));

  axios.get('http://localhost:5000/turnos', { headers })
    .then(res => setTurnos(res.data))
    .catch(err => console.error("Error al obtener turnos:", err));
}, [usuario]);


  return (
    <div className="container mt-4">
      <h3>Panel del Gerente - {usuario.nombre}</h3>

      <div className="mt-4">
        <h5>ðŸ“‹ Reportes Recibidos</h5>
        <ul className="list-group">
          {reportes.map((r, i) => (
            <li key={i} className="list-group-item">
              Guardia: {r.nombre_guardia} - {r.fecha}<br />
              {r.descripcion}
            </li>
          ))}
        </ul>
      </div>

      <div className="mt-4">
        <h5>ðŸ›  Tareas Asignadas</h5>
        <ul className="list-group">
          {tareas.map((t, i) => (
            <li key={i} className="list-group-item">
              Guardia: {t.nombre_guardia} - Tarea: {t.descripcion}
            </li>
          ))}
        </ul>
      </div>

      <div className="mt-4">
        <h5>ðŸ“… Turnos Generales</h5>
        <ul className="list-group">
          {turnos.map((t, i) => (
            <li key={i} className="list-group-item">
              Guardia: {t.nombre_guardia} - Local: {t.local} - Fecha: {t.fecha}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default VistaGerente;
