import React, { useEffect, useState } from 'react';
import axios from 'axios';
import VistaLocales from './VistaLocales';
import GestionUsuarios from './GestionUsuarios';

function VistaGerente({ usuario }) {
  // Estados para datos
  const [guardias, setGuardias] = useState([]);
  const [supervisores, setSupervisores] = useState([]);
  const [turnos, setTurnos] = useState([]);
  const [tareas, setTareas] = useState([]);
  const [reportes, setReportes] = useState([]);
  const [estadisticas, setEstadisticas] = useState(null);
  const [notificacion, setNotificacion] = useState({ tipo: '', mensaje: '' });
  const [cargando, setCargando] = useState(true);

  // Estados para filtros de reportes
  const [filtroFecha, setFiltroFecha] = useState('');
  const [filtroGuardia, setFiltroGuardia] = useState('');
  const [reportesFiltrados, setReportesFiltrados] = useState([]);

  // FunciÃ³n para cargar todos los datos
  const cargarDatos = async () => {
    setCargando(true);
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };

    try {
      const [guardiasRes, supervisoresRes, turnosRes, tareasRes, reportesRes, estadisticasRes] = await Promise.allSettled([
        axios.get('http://localhost:5000/usuarios/guardias', { headers }),
        axios.get('http://localhost:5000/usuarios/supervisores', { headers }),
        axios.get('http://localhost:5000/turnos', { headers }),
        axios.get('http://localhost:5000/tareas', { headers }),
        axios.get('http://localhost:5000/reportes', { headers }),
        axios.get('http://localhost:5000/reportes/estadisticas', { headers })
      ]);

      if (guardiasRes.status === 'fulfilled') setGuardias(guardiasRes.value.data);
      if (supervisoresRes.status === 'fulfilled') setSupervisores(supervisoresRes.value.data);
      if (turnosRes.status === 'fulfilled') setTurnos(turnosRes.value.data);
      if (tareasRes.status === 'fulfilled') setTareas(tareasRes.value.data);
      if (reportesRes.status === 'fulfilled') {
        setReportes(reportesRes.value.data);
        setReportesFiltrados(reportesRes.value.data);
      }
      if (estadisticasRes.status === 'fulfilled') setEstadisticas(estadisticasRes.value.data);

    } catch (error) {
      console.error('Error al cargar datos:', error);
      setNotificacion({ tipo: 'danger', mensaje: 'Error al cargar los datos' });
    } finally {
      setCargando(false);
    }
  };

  useEffect(() => {
    cargarDatos();
  }, [usuario]);

  // FunciÃ³n para filtrar reportes
  const filtrarReportes = () => {
    let filtrados = reportes;

    if (filtroFecha) {
      filtrados = filtrados.filter(reporte => 
        reporte.fecha && reporte.fecha.includes(filtroFecha)
      );
    }

    if (filtroGuardia) {
      filtrados = filtrados.filter(reporte => 
        reporte.nombre_guardia && 
        reporte.nombre_guardia.toLowerCase().includes(filtroGuardia.toLowerCase())
      );
    }

    setReportesFiltrados(filtrados);
  };

  useEffect(() => {
    filtrarReportes();
  }, [filtroFecha, filtroGuardia, reportes]);

  // Cerrar sesiÃ³n
  const cerrarSesion = () => {
    localStorage.removeItem("usuario");
    window.location.reload();
  };

  // Limpiar notificaciÃ³n automÃ¡tica
  useEffect(() => {
    if (notificacion.mensaje) {
      const timer = setTimeout(() => setNotificacion({ tipo: '', mensaje: '' }), 3000);
      return () => clearTimeout(timer);
    }
  }, [notificacion]);

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center">
        <h3>Panel del Gerente - {usuario?.nombre || 'Sin nombre'}</h3>
        <div>
          <button className="btn btn-danger" onClick={cerrarSesion}>Cerrar sesiÃ³n</button>
        </div>
      </div>

      {/* NotificaciÃ³n */}
      {notificacion.mensaje && (
        <div className={`alert alert-${notificacion.tipo} mt-3`} role="alert">
          {notificacion.mensaje}
        </div>
      )}

      {/* Resumen de estadÃ­sticas */}
      <div className="row mt-4">
        <div className="col-md-2 mb-3">
          <div className="card bg-primary text-white">
            <div className="card-body text-center">
              <h4>{guardias.length}</h4>
              <p className="mb-0">ğŸ‘®â€â™‚ï¸ Guardias</p>
            </div>
          </div>
        </div>
        <div className="col-md-2 mb-3">
          <div className="card bg-warning text-dark">
            <div className="card-body text-center">
              <h4>{supervisores.length}</h4>
              <p className="mb-0">ğŸ‘¨â€ğŸ’¼ Supervisores</p>
            </div>
          </div>
        </div>
        <div className="col-md-2 mb-3">
          <div className="card bg-success text-white">
            <div className="card-body text-center">
              <h4>{turnos.length}</h4>
              <p className="mb-0">ğŸ“… Turnos</p>
            </div>
          </div>
        </div>
        <div className="col-md-2 mb-3">
          <div className="card bg-info text-white">
            <div className="card-body text-center">
              <h4>{tareas.length}</h4>
              <p className="mb-0">ğŸ›  Tareas</p>
            </div>
          </div>
        </div>
        <div className="col-md-2 mb-3">
          <div className="card bg-danger text-white">
            <div className="card-body text-center">
              <h4>{reportes.length}</h4>
              <p className="mb-0">ğŸ“ Incidentes</p>
            </div>
          </div>
        </div>
        <div className="col-md-2 mb-3">
          <div className="card bg-secondary text-white">
            <div className="card-body text-center">
              <h4>{guardias.length + supervisores.length}</h4>
              <p className="mb-0">ğŸ‘¥ Total Usuarios</p>
            </div>
          </div>
        </div>
      </div>

      {/* GestiÃ³n de Locales */}
      <VistaLocales usuario={usuario} />

      {/* GestiÃ³n de Usuarios */}
      <GestionUsuarios usuario={usuario} />

      {/* EstadÃ­sticas Detalladas de Incidentes */}
      {estadisticas && (
        <div className="card mt-4">
          <div className="card-header bg-info text-white">
            <h5 className="mb-0">ğŸ“ˆ EstadÃ­sticas de Incidentes</h5>
          </div>
          <div className="card-body">
            <div className="row">
              {/* EstadÃ­sticas generales */}
              <div className="col-md-6">
                <h6>ğŸ“Š Resumen General</h6>
                <div className="row">
                  <div className="col-6">
                    <div className="card bg-light">
                      <div className="card-body text-center">
                        <h4 className="text-primary">{estadisticas.total_reportes}</h4>
                        <small>Total de incidentes</small>
                      </div>
                    </div>
                  </div>
                  <div className="col-6">
                    <div className="card bg-light">
                      <div className="card-body text-center">
                        <h4 className="text-warning">{estadisticas.reportes_ultimos_30_dias}</h4>
                        <small>Ãšltimos 30 dÃ­as</small>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Top guardias con mÃ¡s reportes */}
              <div className="col-md-6">
                <h6>ğŸ‘®â€â™‚ï¸ Guardias con MÃ¡s Incidentes</h6>
                {estadisticas.reportes_por_guardia.length > 0 ? (
                  <div className="list-group">
                    {estadisticas.reportes_por_guardia.slice(0, 5).map((item, index) => (
                      <div key={index} className="list-group-item d-flex justify-content-between align-items-center">
                        <span>
                          <i className="fas fa-user me-2"></i>
                          {item.nombre_guardia || 'Guardia Desconocido'}
                        </span>
                        <span className="badge bg-warning rounded-pill">
                          {item.total_reportes} incidentes
                        </span>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-muted">No hay datos disponibles</p>
                )}
              </div>
            </div>

            {/* GrÃ¡fico de incidentes por dÃ­a */}
            {estadisticas.reportes_por_dia.length > 0 && (
              <div className="mt-4">
                <h6>ğŸ“… Incidentes por DÃ­a (Ãšltimos 7 dÃ­as)</h6>
                <div className="row">
                  {estadisticas.reportes_por_dia.map((item, index) => (
                    <div key={index} className="col-md-2 col-4 mb-2">
                      <div className="card text-center">
                        <div className="card-body">
                          <h6 className="text-info">{item.total}</h6>
                          <small className="text-muted">
                            {new Date(item._id).toLocaleDateString('es-ES', { 
                              day: '2-digit', 
                              month: '2-digit' 
                            })}
                          </small>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Lista de Guardias */}
      <div className="card mt-4">
        <div className="card-header bg-primary text-white">
          <h5 className="mb-0">ğŸ‘®â€â™‚ï¸ Guardias Registrados ({guardias.length})</h5>
        </div>
        <div className="card-body">
          {cargando ? (
            <div className="text-center">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
            </div>
          ) : guardias.length === 0 ? (
            <div className="text-center text-muted py-4">
              <i className="fas fa-users fa-3x mb-3"></i>
              <p>No hay guardias registrados</p>
            </div>
          ) : (
            <div className="row">
              {guardias.map((guardia, i) => (
                <div key={i} className="col-md-6 col-lg-4 mb-3">
                  <div className="card h-100 border-primary">
                    <div className="card-body">
                      <h6 className="card-title">
                        <i className="fas fa-user me-2"></i>
                        {guardia.nombre}
                      </h6>
                      <p className="card-text">
                        <strong>RUT:</strong> {guardia.rut}<br/>
                        <strong>Email:</strong> {guardia.email}<br/>
                        <strong>Rol:</strong> {guardia.rol}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Lista de Turnos */}
      <div className="card mt-4">
        <div className="card-header bg-success text-white">
          <h5 className="mb-0">ğŸ“… Turnos Asignados ({turnos.length})</h5>
        </div>
        <div className="card-body">
          {cargando ? (
            <div className="text-center">
              <div className="spinner-border text-success" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
            </div>
          ) : turnos.length === 0 ? (
            <div className="text-center text-muted py-4">
              <i className="fas fa-calendar-alt fa-3x mb-3"></i>
              <p>No hay turnos asignados</p>
            </div>
          ) : (
            <div className="row">
              {turnos.map((turno, i) => (
                <div key={i} className="col-md-6 col-lg-4 mb-3">
                  <div className="card h-100 border-success">
                    <div className="card-header bg-success text-white">
                      <h6 className="mb-0">Turno #{i + 1}</h6>
                    </div>
                    <div className="card-body">
                      <div className="mb-2">
                        <strong>ğŸ‘¤ Guardia:</strong> {turno.nombre_guardia}
                      </div>
                      <div className="mb-2">
                        <strong>ğŸ¢ Local:</strong> {turno.nombre_local || turno.local || turno.id_local}
                      </div>
                      <div className="mb-2">
                        <strong>ğŸ“… Fecha:</strong> {new Date(turno.fecha).toLocaleDateString('es-ES')}
                      </div>
                      <div className="mb-2">
                        <strong>â° Horario:</strong> {turno.hora_entrada} - {turno.hora_salida}
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default VistaGerente;
