import React, { useState, useEffect } from 'react';
import axios from 'axios';

function GestionUsuarios({ usuario }) {
  // Estados para datos
  const [usuarios, setUsuarios] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [notificacion, setNotificacion] = useState({ tipo: '', mensaje: '' });
  
  // Estados para formulario
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [editandoUsuario, setEditandoUsuario] = useState(null);
  const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    password: '',
    rut: '',
    rol: 'guardia',
    telefono: '',
    direccion: ''
  });

  // Estados para filtros
  const [filtroRol, setFiltroRol] = useState('');
  const [filtroNombre, setFiltroNombre] = useState('');
  const [usuariosFiltrados, setUsuariosFiltrados] = useState([]);

  // Cargar usuarios
  const cargarUsuarios = async () => {
    setCargando(true);
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    
    try {
      const response = await axios.get('http://localhost:5000/usuarios/todos', { headers });
      setUsuarios(response.data);
      setUsuariosFiltrados(response.data);
    } catch (error) {
      setNotificacion({ tipo: 'danger', mensaje: 'Error al cargar usuarios' });
    } finally {
      setCargando(false);
    }
  };

  useEffect(() => {
    cargarUsuarios();
  }, [usuario]);

  // Filtrar usuarios
  const filtrarUsuarios = () => {
    let filtrados = usuarios;

    if (filtroRol) {
      filtrados = filtrados.filter(user => user.rol === filtroRol);
    }

    if (filtroNombre) {
      filtrados = filtrados.filter(user => 
        user.nombre && user.nombre.toLowerCase().includes(filtroNombre.toLowerCase())
      );
    }

    setUsuariosFiltrados(filtrados);
  };

  useEffect(() => {
    filtrarUsuarios();
  }, [filtroRol, filtroNombre, usuarios]);

  // Limpiar formulario
  const limpiarFormulario = () => {
    setFormData({
      nombre: '',
      email: '',
      password: '',
      rut: '',
      rol: 'guardia',
      telefono: '',
      direccion: ''
    });
    setEditandoUsuario(null);
  };

  // Abrir formulario para crear
  const abrirFormularioCrear = () => {
    limpiarFormulario();
    setMostrarFormulario(true);
  };

  // Abrir formulario para editar
  const abrirFormularioEditar = (usuario) => {
    setFormData({
      nombre: usuario.nombre || '',
      email: usuario.email || '',
      password: '',
      rut: usuario.rut || '',
      rol: usuario.rol || 'guardia',
      telefono: usuario.telefono || '',
      direccion: usuario.direccion || ''
    });
    setEditandoUsuario(usuario);
    setMostrarFormulario(true);
  };

  // Cerrar formulario
  const cerrarFormulario = () => {
    setMostrarFormulario(false);
    limpiarFormulario();
  };

  // Manejar cambios en el formulario
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Crear o editar usuario
  const handleSubmit = async (e) => {
    e.preventDefault();
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    try {
      if (editandoUsuario) {
        await axios.put(`http://localhost:5000/usuarios/${editandoUsuario._id}`, formData, { headers });
        setNotificacion({ tipo: 'success', mensaje: 'Usuario actualizado' });
      } else {
        await axios.post('http://localhost:5000/usuarios', formData, { headers });
        setNotificacion({ tipo: 'success', mensaje: 'Usuario creado' });
      }
      cargarUsuarios();
      cerrarFormulario();
    } catch (error) {
      setNotificacion({ tipo: 'danger', mensaje: 'Error al guardar usuario' });
    }
  };

  // Eliminar usuario
  const eliminarUsuario = async (usuarioId, nombreUsuario) => {
    if (!window.confirm(`¿Eliminar usuario "${nombreUsuario}"?`)) return;
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    try {
      await axios.delete(`http://localhost:5000/usuarios/${usuarioId}`, { headers });
      setNotificacion({ tipo: 'success', mensaje: 'Usuario eliminado' });
      cargarUsuarios();
    } catch (error) {
      setNotificacion({ tipo: 'danger', mensaje: 'Error al eliminar usuario' });
    }
  };

  // Limpiar notificación automática
  useEffect(() => {
    if (notificacion.mensaje) {
      const timer = setTimeout(() => setNotificacion({ tipo: '', mensaje: '' }), 3000);
      return () => clearTimeout(timer);
    }
  }, [notificacion]);

  return (
    <div style={{ maxWidth: 700, margin: '30px auto', background: '#fff', border: '1px solid #ddd', borderRadius: 8, boxShadow: '0 2px 8px #eee' }}>
      <div style={{ borderBottom: '1px solid #eee', padding: 16, display: 'flex', justifyContent: 'space-between', alignItems: 'center', background: '#f7f7f7' }}>
        <span style={{ fontWeight: 600, fontSize: 20 }}>Gestión de Usuarios</span>
        <div>
          <button onClick={cargarUsuarios} disabled={cargando} style={{ marginRight: 8, padding: '4px 12px' }}>Actualizar</button>
          <button onClick={abrirFormularioCrear} style={{ padding: '4px 12px' }}>Nuevo</button>
        </div>
      </div>
      <div style={{ padding: 16 }}>
        {notificacion.mensaje && (
          <div style={{ marginBottom: 16, color: notificacion.tipo === 'danger' ? '#b94a48' : '#468847', background: notificacion.tipo === 'danger' ? '#f2dede' : '#dff0d8', padding: 8, borderRadius: 4, fontSize: 15 }}>{notificacion.mensaje}</div>
        )}
        {mostrarFormulario && (
          <form onSubmit={handleSubmit} style={{ marginBottom: 24, background: '#fafafa', padding: 16, borderRadius: 6, border: '1px solid #eee' }}>
            <div style={{ display: 'flex', flexWrap: 'wrap', gap: 12 }}>
              <input name="nombre" value={formData.nombre} onChange={handleInputChange} placeholder="Nombre" required style={{ flex: 1, minWidth: 120 }} />
              <input name="email" value={formData.email} onChange={handleInputChange} placeholder="Email" type="email" required style={{ flex: 1, minWidth: 120 }} />
              <input name="password" value={formData.password} onChange={handleInputChange} placeholder="Contraseña" type="password" required={!editandoUsuario} style={{ flex: 1, minWidth: 120 }} />
              <input name="rut" value={formData.rut} onChange={handleInputChange} placeholder="RUT" style={{ flex: 1, minWidth: 120 }} />
              <select name="rol" value={formData.rol} onChange={handleInputChange} style={{ flex: 1, minWidth: 120 }}>
                <option value="guardia">Guardia</option>
                <option value="supervisor">Supervisor</option>
                <option value="gerente">Gerente</option>
              </select>
              <input name="telefono" value={formData.telefono} onChange={handleInputChange} placeholder="Teléfono" style={{ flex: 1, minWidth: 120 }} />
              <input name="direccion" value={formData.direccion} onChange={handleInputChange} placeholder="Dirección" style={{ flex: 1, minWidth: 120 }} />
            </div>
            <div style={{ marginTop: 16, display: 'flex', gap: 8 }}>
              <button type="submit" style={{ padding: '6px 18px' }}>{editandoUsuario ? 'Guardar cambios' : 'Crear usuario'}</button>
              <button type="button" onClick={cerrarFormulario} style={{ padding: '6px 18px', background: '#eee' }}>Cancelar</button>
            </div>
          </form>
        )}
        <table style={{ width: '100%', borderCollapse: 'collapse', fontSize: 15 }}>
          <thead>
            <tr style={{ background: '#f7f7f7' }}>
              <th style={{ padding: 8, border: '1px solid #eee' }}>Nombre</th>
              <th style={{ padding: 8, border: '1px solid #eee' }}>Email</th>
              <th style={{ padding: 8, border: '1px solid #eee' }}>Rol</th>
              <th style={{ padding: 8, border: '1px solid #eee' }}>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {usuarios.length === 0 ? (
              <tr><td colSpan={4} style={{ textAlign: 'center', padding: 16 }}>No hay usuarios registrados.</td></tr>
            ) : (
              usuarios.map(user => (
                <tr key={user._id}>
                  <td style={{ padding: 8, border: '1px solid #eee' }}>{user.nombre}</td>
                  <td style={{ padding: 8, border: '1px solid #eee' }}>{user.email}</td>
                  <td style={{ padding: 8, border: '1px solid #eee' }}>{user.rol}</td>
                  <td style={{ padding: 8, border: '1px solid #eee' }}>
                    <button onClick={() => abrirFormularioEditar(user)} style={{ marginRight: 8, padding: '2px 10px' }}>Editar</button>
                    <button onClick={() => eliminarUsuario(user._id, user.nombre)} style={{ padding: '2px 10px', background: '#eee' }}>Eliminar</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default GestionUsuarios;
