import React, { useEffect, useState } from 'react';
import axios from 'axios';
import VistaLocales from './VistaLocales';

function VistaSupervisor({ usuario }) {
  // Listas y estado de la app
  const [tareas, setTareas] = useState([]);
  const [turnos, setTurnos] = useState([]);
  const [guardias, setGuardias] = useState([]);
  const [locales, setLocales] = useState([]);
  const [notificacion, setNotificacion] = useState({ tipo: '', mensaje: '' });

  // Estado para asignar turno (solo turno, sin tareas)
  const [formTurno, setFormTurno] = useState({
    id_guardia: '',
    id_local: '',
    fecha: '',
    hora_entrada: '',
    hora_salida: '',
  });

  // Estado para asignar tarea (solo tarea)
  const [formTarea, setFormTarea] = useState({
    id_guardia: '',
    id_local: '',
    descripcion: '',
    fecha: '', // Opcional si tu backend lo requiere
  });

  // Estado para múltiples tareas
  const [tareasMultiples, setTareasMultiples] = useState([]);
  const [formTareaMultiple, setFormTareaMultiple] = useState({
    id_guardia: '',
    id_local: '',
    descripcion: '',
    fecha: '',
  });

  // Cargar datos iniciales
  useEffect(() => {
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    axios.get('http://localhost:5000/tareas', { headers }).then(res => setTareas(res.data));
    axios.get('http://localhost:5000/turnos', { headers }).then(res => setTurnos(res.data));
    axios.get('http://localhost:5000/usuarios/guardias', { headers }).then(res => setGuardias(res.data));
    axios.get('http://localhost:5000/locales', { headers }).then(res => setLocales(res.data));
  }, [usuario]);

  // Cerrar sesión
  const cerrarSesion = () => {
    localStorage.removeItem("usuario");
    window.location.reload();
  };

  // ----- ASIGNAR TURNO -----
  const handleChangeTurno = e => {
    setFormTurno({ ...formTurno, [e.target.name]: e.target.value });
  };

  const handleSubmitTurno = async e => {
    e.preventDefault();
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    try {
      await axios.post('http://localhost:5000/turnos/asignar', formTurno, { headers });
      setNotificacion({ tipo: 'success', mensaje: 'Turno asignado correctamente' });
      setFormTurno({ id_guardia: '', id_local: '', fecha: '', hora_entrada: '', hora_salida: '' });
      // Recargar turnos
      const res = await axios.get('http://localhost:5000/turnos', { headers });
      setTurnos(res.data);
    } catch (error) {
      let msg = error.response?.data?.error || 'Error al asignar turno';
      setNotificacion({ tipo: 'danger', mensaje: msg });
    }
  };

  // ----- ASIGNAR TAREA -----
  const handleChangeTarea = e => {
    setFormTarea({ ...formTarea, [e.target.name]: e.target.value });
  };

  const handleSubmitTarea = async e => {
    e.preventDefault();
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    try {
      await axios.post('http://localhost:5000/tareas/asignar', formTarea, { headers });
      setNotificacion({ tipo: 'success', mensaje: 'Tarea asignada correctamente' });
      setFormTarea({ id_guardia: '', id_local: '', descripcion: '', fecha: '' });
      // Recargar tareas
      const res = await axios.get('http://localhost:5000/tareas', { headers });
      setTareas(res.data);
    } catch (error) {
      let msg = error.response?.data?.error || 'Error al asignar tarea';
      setNotificacion({ tipo: 'danger', mensaje: msg });
    }
  };

  // ----- MÚLTIPLES TAREAS -----
  const handleChangeTareaMultiple = e => {
    setFormTareaMultiple({ ...formTareaMultiple, [e.target.name]: e.target.value });
  };

  const agregarTareaALista = () => {
    if (!formTareaMultiple.id_guardia || !formTareaMultiple.id_local || !formTareaMultiple.descripcion) {
      setNotificacion({ tipo: 'warning', mensaje: 'Por favor completa todos los campos obligatorios' });
      return;
    }

    const nuevaTarea = {
      ...formTareaMultiple,
      id: Date.now(), // ID temporal para identificar la tarea en la lista
    };

    setTareasMultiples([...tareasMultiples, nuevaTarea]);
    setFormTareaMultiple({ id_guardia: '', id_local: '', descripcion: '', fecha: '' });
    setNotificacion({ tipo: 'success', mensaje: 'Tarea agregada a la lista' });
  };

  const eliminarTareaDeLista = (id) => {
    setTareasMultiples(tareasMultiples.filter(tarea => tarea.id !== id));
  };

  const enviarTodasLasTareas = async () => {
    if (tareasMultiples.length === 0) {
      setNotificacion({ tipo: 'warning', mensaje: 'No hay tareas en la lista para enviar' });
      return;
    }

    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    let tareasExitosas = 0;
    let tareasFallidas = 0;

    try {
      for (const tarea of tareasMultiples) {
        try {
          const { id, ...tareaSinId } = tarea; // Remover el ID temporal
          await axios.post('http://localhost:5000/tareas/asignar', tareaSinId, { headers });
          tareasExitosas++;
        } catch (error) {
          tareasFallidas++;
          console.error('Error al asignar tarea:', error);
        }
      }

      if (tareasExitosas > 0) {
        setNotificacion({ 
          tipo: 'success', 
          mensaje: `${tareasExitosas} tareas asignadas correctamente${tareasFallidas > 0 ? `, ${tareasFallidas} fallaron` : ''}` 
        });
        setTareasMultiples([]);
        // Recargar tareas
        const res = await axios.get('http://localhost:5000/tareas', { headers });
        setTareas(res.data);
      } else {
        setNotificacion({ tipo: 'danger', mensaje: 'No se pudo asignar ninguna tarea' });
      }
    } catch (error) {
      setNotificacion({ tipo: 'danger', mensaje: 'Error al procesar las tareas' });
    }
  };

  const limpiarListaTareas = () => {
    setTareasMultiples([]);
    setNotificacion({ tipo: 'info', mensaje: 'Lista de tareas limpiada' });
  };

  // Limpiar notificación automática
  useEffect(() => {
    if (notificacion.mensaje) {
      const timer = setTimeout(() => setNotificacion({ tipo: '', mensaje: '' }), 3000);
      return () => clearTimeout(timer);
    }
  }, [notificacion]);

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center">
        <h3>Panel del Supervisor - {usuario.nombre}</h3>
        <button className="btn btn-danger" onClick={cerrarSesion}>Cerrar sesión</button>
      </div>

      {/* Notificación */}
      {notificacion.mensaje && (
        <div className={`alert alert-${notificacion.tipo} mt-3`} role="alert">
          {notificacion.mensaje}
        </div>
      )}

      {/* FORMULARIO PARA ASIGNAR TURNO */}
      <div className="card mt-4">
        <div className="card-body">
          <h5>Asignar Turno</h5>
          <form onSubmit={handleSubmitTurno}>
            <div className="row g-2">
              <div className="col-md-3">
                <label className="form-label">Guardia</label>
                <select
                  className="form-select"
                  name="id_guardia"
                  value={formTurno.id_guardia}
                  onChange={handleChangeTurno}
                  required
                >
                  <option value="">Selecciona</option>
                  {guardias.map(g => (
                    <option key={g._id} value={g._id}>{g.nombre}</option>
                  ))}
                </select>
              </div>
              <div className="col-md-3">
                <label className="form-label">Local</label>
                <select
                  className="form-select"
                  name="id_local"
                  value={formTurno.id_local}
                  onChange={handleChangeTurno}
                  required
                >
                  <option value="">Selecciona</option>
                  {locales.map(l => (
                    <option key={l._id} value={l._id}>{l.nombre}</option>
                  ))}
                </select>
              </div>
              <div className="col-md-2">
                <label className="form-label">Fecha</label>
                <input
                  type="date"
                  className="form-control"
                  name="fecha"
                  value={formTurno.fecha}
                  onChange={handleChangeTurno}
                  required
                />
              </div>
              <div className="col-md-2">
                <label className="form-label">Hora Entrada</label>
                <input
                  type="time"
                  className="form-control"
                  name="hora_entrada"
                  value={formTurno.hora_entrada}
                  onChange={handleChangeTurno}
                  required
                />
              </div>
              <div className="col-md-2">
                <label className="form-label">Hora Salida</label>
                <input
                  type="time"
                  className="form-control"
                  name="hora_salida"
                  value={formTurno.hora_salida}
                  onChange={handleChangeTurno}
                  required
                />
              </div>
            </div>
            <button type="submit" className="btn btn-success mt-2">Asignar Turno</button>
          </form>
        </div>
      </div>

      {/* FORMULARIO PARA ASIGNAR TAREA INDIVIDUAL */}
      <div className="card mt-4">
        <div className="card-body">
          <h5>Asignar Tarea Individual</h5>
          <form onSubmit={handleSubmitTarea}>
            <div className="row g-2">
              <div className="col-md-3">
                <label className="form-label">Guardia</label>
                <select
                  className="form-select"
                  name="id_guardia"
                  value={formTarea.id_guardia}
                  onChange={handleChangeTarea}
                  required
                >
                  <option value="">Selecciona</option>
                  {guardias.map(g => (
                    <option key={g._id} value={g._id}>{g.nombre}</option>
                  ))}
                </select>
              </div>
              <div className="col-md-3">
                <label className="form-label">Local</label>
                <select
                  className="form-select"
                  name="id_local"
                  value={formTarea.id_local}
                  onChange={handleChangeTarea}
                  required
                >
                  <option value="">Selecciona</option>
                  {locales.map(l => (
                    <option key={l._id} value={l._id}>{l.nombre}</option>
                  ))}
                </select>
              </div>
              <div className="col-md-4">
                <label className="form-label">Descripción de la tarea</label>
                <input
                  type="text"
                  className="form-control"
                  name="descripcion"
                  value={formTarea.descripcion}
                  onChange={handleChangeTarea}
                  required
                />
              </div>
              <div className="col-md-2">
                <label className="form-label">Fecha</label>
                <input
                  type="date"
                  className="form-control"
                  name="fecha"
                  value={formTarea.fecha}
                  onChange={handleChangeTarea}
                />
              </div>
            </div>
            <button type="submit" className="btn btn-primary mt-2">Asignar Tarea</button>
          </form>
        </div>
      </div>

      {/* FORMULARIO PARA MÚLTIPLES TAREAS */}
      <div className="card mt-4">
        <div className="card-body">
          <h5>Asignar Múltiples Tareas</h5>
          <div className="row g-2">
            <div className="col-md-3">
              <label className="form-label">Guardia</label>
              <select
                className="form-select"
                name="id_guardia"
                value={formTareaMultiple.id_guardia}
                onChange={handleChangeTareaMultiple}
                required
              >
                <option value="">Selecciona</option>
                {guardias.map(g => (
                  <option key={g._id} value={g._id}>{g.nombre}</option>
                ))}
              </select>
            </div>
            <div className="col-md-3">
              <label className="form-label">Local</label>
              <select
                className="form-select"
                name="id_local"
                value={formTareaMultiple.id_local}
                onChange={handleChangeTareaMultiple}
                required
              >
                <option value="">Selecciona</option>
                {locales.map(l => (
                  <option key={l._id} value={l._id}>{l.nombre}</option>
                ))}
              </select>
            </div>
            <div className="col-md-4">
              <label className="form-label">Descripción de la tarea</label>
              <input
                type="text"
                className="form-control"
                name="descripcion"
                value={formTareaMultiple.descripcion}
                onChange={handleChangeTareaMultiple}
                required
              />
            </div>
            <div className="col-md-2">
              <label className="form-label">Fecha</label>
              <input
                type="date"
                className="form-control"
                name="fecha"
                value={formTareaMultiple.fecha}
                onChange={handleChangeTareaMultiple}
              />
            </div>
          </div>
          <div className="mt-2">
            <button 
              type="button" 
              className="btn btn-info me-2" 
              onClick={agregarTareaALista}
            >
              ➕ Agregar a Lista
            </button>
            <button 
              type="button" 
              className="btn btn-success me-2" 
              onClick={enviarTodasLasTareas}
              disabled={tareasMultiples.length === 0}
            >
              📤 Enviar Todas las Tareas ({tareasMultiples.length})
            </button>
            <button 
              type="button" 
              className="btn btn-warning" 
              onClick={limpiarListaTareas}
              disabled={tareasMultiples.length === 0}
            >
              🗑️ Limpiar Lista
            </button>
          </div>

          {/* Lista de tareas pendientes */}
          {tareasMultiples.length > 0 && (
            <div className="mt-3">
              <h6>Tareas en lista ({tareasMultiples.length}):</h6>
              <div className="list-group">
                {tareasMultiples.map((tarea, index) => {
                  const guardia = guardias.find(g => g._id === tarea.id_guardia);
                  const local = locales.find(l => l._id === tarea.id_local);
                  return (
                    <div key={tarea.id} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <strong>{index + 1}.</strong> {guardia?.nombre} - {local?.nombre} - {tarea.descripcion}
                        {tarea.fecha && <span className="text-muted ms-2">({tarea.fecha})</span>}
                      </div>
                      <button 
                        className="btn btn-sm btn-outline-danger" 
                        onClick={() => eliminarTareaDeLista(tarea.id)}
                      >
                        ❌
                      </button>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Gestión de Locales */}
      <VistaLocales usuario={usuario} />

      {/* Lista de tareas */}
      <h5 className="mt-4">🛠 Tareas</h5>
      <ul className="list-group">
        {tareas.length > 0 ? (
          tareas.map((t, i) => (
            <li key={i} className="list-group-item">
              Guardia: {t.nombre_guardia} - Local: {t.nombre_local} - Tarea: {t.descripcion}
            </li>
          ))
        ) : (
          <li className="list-group-item text-muted">No hay tareas asignadas</li>
        )}
      </ul>

      {/* Lista de turnos */}
      <h5 className="mt-4">📅 Turnos</h5>
      <ul className="list-group">
        {turnos.length > 0 ? (
          turnos.map((t, i) => (
            <li key={i} className="list-group-item">
              Guardia: {t.nombre_guardia} - Local: {t.local || t.id_local} - Fecha: {t.fecha}
            </li>
          ))
        ) : (
          <li className="list-group-item text-muted">No hay turnos asignados</li>
        )}
      </ul>
    </div>
  );
}

export default VistaSupervisor;
