import React, { useEffect, useState } from 'react';
import axios from 'axios';

function VistaGuardia({ usuario }) {
  const [turnos, setTurnos] = useState([]);
  const [tareas, setTareas] = useState([]);
  const [descripcion, setDescripcion] = useState('');
  const [turnoActual, setTurnoActual] = useState(null);
  const [turnoActualError, setTurnoActualError] = useState('');
  const [notificacion, setNotificacion] = useState({ tipo: '', mensaje: '' });
  const [cargando, setCargando] = useState(true);

  // Función para cargar todos los datos
  const cargarDatos = async () => {
    if (!usuario || !usuario.id) {
      console.log("Usuario aún no cargado o no tiene id");
      return;
    }

    setCargando(true);
    const headers = {
      Rol: usuario.rol,
      Usuario_Id: usuario.id
    };

    console.log('=== DEBUG: Cargar datos del guardia ===');
    console.log('Usuario:', usuario);
    console.log('Headers:', headers);

    try {
      // Cargar turnos y tareas en paralelo
      const [turnosRes, tareasRes, turnoActualRes] = await Promise.allSettled([
        axios.get('http://localhost:5000/turnos/mis-turnos', { headers }),
        axios.get('http://localhost:5000/tareas/mis-tareas', { headers }),
        axios.get('http://localhost:5000/turnos/mi-turno-actual', { headers })
      ]);

      // Procesar turnos
      if (turnosRes.status === 'fulfilled') {
        console.log('✅ Turnos cargados exitosamente:', turnosRes.value.data);
        setTurnos(turnosRes.value.data);
      } else {
        console.error('❌ Error obteniendo turnos:', turnosRes.reason);
        console.error('Response data:', turnosRes.reason.response?.data);
        console.error('Response status:', turnosRes.reason.response?.status);
      }

      // Procesar tareas
      if (tareasRes.status === 'fulfilled') {
        console.log('✅ Tareas cargadas exitosamente:', tareasRes.value.data);
        setTareas(tareasRes.value.data);
      } else {
        console.error('❌ Error obteniendo tareas:', tareasRes.reason);
        console.error('Response data:', tareasRes.reason.response?.data);
        console.error('Response status:', tareasRes.reason.response?.status);
      }

      // Procesar turno actual
      if (turnoActualRes.status === 'fulfilled') {
        console.log('✅ Turno actual cargado exitosamente:', turnoActualRes.value.data);
        setTurnoActual(turnoActualRes.value.data);
        setTurnoActualError('');
      } else {
        console.error('❌ Error obteniendo turno actual:', turnoActualRes.reason);
        console.error('Response data:', turnoActualRes.reason.response?.data);
        console.error('Response status:', turnoActualRes.reason.response?.status);
        
        setTurnoActual(null);
        setTurnoActualError(
          turnoActualRes.reason.response?.data?.mensaje ||
          "No se pudo obtener tu turno actual."
        );
      }

    } catch (error) {
      console.error('Error general al cargar datos:', error);
      setNotificacion({ tipo: 'danger', mensaje: 'Error al cargar los datos' });
    } finally {
      setCargando(false);
    }
  };

  // Función de prueba para verificar conexión del guardia
  const probarConexionGuardia = () => {
    console.log('=== PRUEBA DE CONEXIÓN GUARDIA ===');
    console.log('Usuario actual:', usuario);
    console.log('Rol del usuario:', usuario?.rol);
    console.log('ID del usuario:', usuario?.id);
    
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    console.log('Headers a enviar:', headers);
    
    // Probar GET de turnos
    axios.get('http://localhost:5000/turnos/mis-turnos', { headers })
      .then(res => {
        console.log('✅ GET mis-turnos exitoso:', res.data);
        console.log('Número de turnos:', res.data.length);
      })
      .catch(err => {
        console.error('❌ Error en GET mis-turnos:', err);
        console.error('Response data:', err.response?.data);
        console.error('Response status:', err.response?.status);
      });
    
    // Probar GET de turno actual
    axios.get('http://localhost:5000/turnos/mi-turno-actual', { headers })
      .then(res => {
        console.log('✅ GET mi-turno-actual exitoso:', res.data);
      })
      .catch(err => {
        console.error('❌ Error en GET mi-turno-actual:', err);
        console.error('Response data:', err.response?.data);
        console.error('Response status:', err.response?.status);
      });
    
    // Probar GET de tareas
    axios.get('http://localhost:5000/tareas/mis-tareas', { headers })
      .then(res => {
        console.log('✅ GET mis-tareas exitoso:', res.data);
        console.log('Número de tareas:', res.data.length);
      })
      .catch(err => {
        console.error('❌ Error en GET mis-tareas:', err);
        console.error('Response data:', err.response?.data);
        console.error('Response status:', err.response?.status);
      });
  };

  useEffect(() => {
    cargarDatos();
  }, [usuario]);

  const enviarReporte = async () => {
    if (!usuario || !usuario.id) {
      setNotificacion({ tipo: 'danger', mensaje: 'Usuario no definido' });
      return;
    }

    if (!descripcion.trim()) {
      setNotificacion({ tipo: 'warning', mensaje: 'Por favor ingresa una descripción del reporte' });
      return;
    }

    const headers = {
      Rol: usuario.rol,
      Usuario_Id: usuario.id
    };

    try {
      await axios.post('http://localhost:5000/reportes/enviar', {
        descripcion,
        fecha: new Date().toISOString()
      }, { headers });
      
      setNotificacion({ tipo: 'success', mensaje: 'Reporte enviado correctamente' });
      setDescripcion('');
    } catch (error) {
      setNotificacion({ 
        tipo: 'danger', 
        mensaje: error.response?.data?.error || 'Error al enviar el reporte' 
      });
    }
  };

  const cerrarSesion = () => {
    localStorage.removeItem("usuario");
    window.location.reload();
  };

  // Limpiar notificación automática
  useEffect(() => {
    if (notificacion.mensaje) {
      const timer = setTimeout(() => setNotificacion({ tipo: '', mensaje: '' }), 3000);
      return () => clearTimeout(timer);
    }
  }, [notificacion]);

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center">
        <h3>Panel del Guardia - {usuario?.nombre || 'Sin nombre'}</h3>
        <div>
          <button className="btn btn-info me-2" onClick={cargarDatos} disabled={cargando}>
            <i className="fas fa-sync-alt"></i> Actualizar
          </button>
          <button className="btn btn-danger" onClick={cerrarSesion}>Cerrar sesión</button>
        </div>
      </div>

      {/* Notificación */}
      {notificacion.mensaje && (
        <div className={`alert alert-${notificacion.tipo} mt-3`} role="alert">
          {notificacion.mensaje}
        </div>
      )}

      {/* --- Tarjeta de turno actual --- */}
      <div className="card mt-4">
        <div className="card-header bg-primary text-white">
          <h5 className="mb-0">🕐 Mi Turno Actual</h5>
        </div>
        <div className="card-body">
          {cargando ? (
            <div className="text-center">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
              <p className="mt-2">Cargando información...</p>
            </div>
          ) : turnoActualError ? (
            <div className="alert alert-warning mb-0">
              <i className="fas fa-exclamation-triangle me-2"></i>
              {turnoActualError}
            </div>
          ) : turnoActual ? (
            <div className="row">
              <div className="col-md-6">
                <h6>🏢 Información del Local</h6>
                <p><strong>Nombre:</strong> {turnoActual.local.nombre}</p>
                <p><strong>Dirección:</strong> {turnoActual.local.direccion}</p>
              </div>
              <div className="col-md-6">
                <h6>📅 Información del Turno</h6>
                <p><strong>Fecha:</strong> {new Date(turnoActual.turno.fecha).toLocaleDateString('es-ES')}</p>
                <p><strong>Horario:</strong> {turnoActual.turno.hora_entrada} - {turnoActual.turno.hora_salida}</p>
              </div>
              {turnoActual.turno.tareas && turnoActual.turno.tareas.length > 0 && (
                <div className="col-12 mt-3">
                  <h6>📋 Tareas del Turno</h6>
                  <ul className="list-group">
                    {turnoActual.turno.tareas.map((tarea, idx) => (
                      <li key={idx} className="list-group-item">
                        <i className="fas fa-check-circle text-success me-2"></i>
                        {tarea}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ) : (
            <div className="text-center text-muted">
              <i className="fas fa-calendar-times fa-3x mb-3"></i>
              <p>No tienes un turno activo en este momento</p>
            </div>
          )}
        </div>
      </div>

      {/* --- Lista de todos los turnos --- */}
      <div className="card mt-4">
        <div className="card-header bg-success text-white">
          <h5 className="mb-0">📅 Mis Turnos ({turnos.length})</h5>
        </div>
        <div className="card-body">
          {cargando ? (
            <div className="text-center">
              <div className="spinner-border text-success" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
            </div>
          ) : turnos.length === 0 ? (
            <div className="text-center text-muted py-4">
              <i className="fas fa-calendar-alt fa-3x mb-3"></i>
              <p>No tienes turnos asignados</p>
            </div>
          ) : (
            <div className="row">
              {turnos.map((turno, i) => (
                <div key={i} className="col-md-6 col-lg-4 mb-3">
                  <div className="card h-100 border-success">
                    <div className="card-header bg-success text-white">
                      <h6 className="mb-0">Turno #{i + 1}</h6>
                    </div>
                    <div className="card-body">
                      <div className="mb-2">
                        <strong>🏢 Local:</strong> {turno.nombre_local || turno.local || turno.id_local}
                      </div>
                      <div className="mb-2">
                        <strong>📅 Fecha:</strong> {new Date(turno.fecha).toLocaleDateString('es-ES')}
                      </div>
                      <div className="mb-2">
                        <strong>⏰ Horario:</strong>
                        <div className="ms-3">
                          <div>🟢 Entrada: {turno.hora_entrada}</div>
                          <div>🔴 Salida: {turno.hora_salida}</div>
                        </div>
                      </div>
                      {turno.tareas && turno.tareas.length > 0 && (
                        <div className="mb-2">
                          <strong>📋 Tareas:</strong>
                          <ul className="list-unstyled ms-3 mt-1">
                            {turno.tareas.map((tarea, idx) => (
                              <li key={idx}>
                                <i className="fas fa-check text-success me-1"></i>
                                {tarea}
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* --- Lista de tareas asignadas --- */}
      <div className="card mt-4">
        <div className="card-header bg-info text-white">
          <h5 className="mb-0">🛠 Mis Tareas ({tareas.length})</h5>
        </div>
        <div className="card-body">
          {cargando ? (
            <div className="text-center">
              <div className="spinner-border text-info" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
            </div>
          ) : tareas.length === 0 ? (
            <div className="text-center text-muted py-4">
              <i className="fas fa-tasks fa-3x mb-3"></i>
              <p>No tienes tareas asignadas</p>
            </div>
          ) : (
            <div className="row">
              {tareas.map((tarea, i) => (
                <div key={i} className="col-md-6 col-lg-4 mb-3">
                  <div className="card h-100 border-info">
                    <div className="card-header bg-info text-white">
                      <h6 className="mb-0">Tarea #{i + 1}</h6>
                    </div>
                    <div className="card-body">
                      <div className="mb-2">
                        <strong>🏢 Local:</strong> {tarea.nombre_local}
                      </div>
                      <div className="mb-2">
                        <strong>📝 Descripción:</strong> {tarea.descripcion}
                      </div>
                      {tarea.fecha && (
                        <div className="mb-2">
                          <strong>📅 Fecha:</strong> {new Date(tarea.fecha).toLocaleDateString('es-ES')}
                        </div>
                      )}
                      {tarea.estado && (
                        <div className="mb-2">
                          <strong>Estado:</strong> 
                          <span className={`badge ms-2 ${
                            tarea.estado === 'completada' ? 'bg-success' : 
                            tarea.estado === 'en_progreso' ? 'bg-warning' : 'bg-secondary'
                          }`}>
                            {tarea.estado}
                          </span>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* --- Reporte de incidentes --- */}
      <div className="card mt-4">
        <div className="card-header bg-warning text-dark">
          <h5 className="mb-0">📝 Enviar Reporte de Incidente</h5>
        </div>
        <div className="card-body">
          <div className="mb-3">
            <label className="form-label">Descripción del incidente:</label>
            <textarea
              className="form-control"
              rows="4"
              value={descripcion}
              onChange={e => setDescripcion(e.target.value)}
              placeholder="Describe el incidente o situación que necesitas reportar..."
            />
          </div>
          <button 
            className="btn btn-warning" 
            onClick={enviarReporte}
            disabled={!descripcion.trim()}
          >
            <i className="fas fa-paper-plane me-2"></i>
            Enviar Reporte
          </button>
        </div>
      </div>
    </div>
  );
}

export default VistaGuardia;
