import React, { useEffect, useState } from 'react';
import axios from 'axios';
import VistaLocales from './VistaLocales';

function VistaGerente({ usuario }) {
  // Estados para datos
  const [guardias, setGuardias] = useState([]);
  const [turnos, setTurnos] = useState([]);
  const [tareas, setTareas] = useState([]);
  const [reportes, setReportes] = useState([]);
  const [estadisticas, setEstadisticas] = useState(null);
  const [notificacion, setNotificacion] = useState({ tipo: '', mensaje: '' });
  const [cargando, setCargando] = useState(true);

  // Estados para filtros de reportes
  const [filtroFecha, setFiltroFecha] = useState('');
  const [filtroGuardia, setFiltroGuardia] = useState('');
  const [reportesFiltrados, setReportesFiltrados] = useState([]);

  // Función para cargar todos los datos
  const cargarDatos = async () => {
    setCargando(true);
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    
    try {
      const [guardiasRes, turnosRes, tareasRes, reportesRes] = await Promise.allSettled([
        axios.get('http://localhost:5000/usuarios/guardias', { headers }),
        axios.get('http://localhost:5000/turnos', { headers }),
        axios.get('http://localhost:5000/tareas', { headers }),
        axios.get('http://localhost:5000/reportes', { headers })
      ]);

      if (guardiasRes.status === 'fulfilled') setGuardias(guardiasRes.value.data);
      if (turnosRes.status === 'fulfilled') setTurnos(turnosRes.value.data);
      if (tareasRes.status === 'fulfilled') setTareas(tareasRes.value.data);
      if (reportesRes.status === 'fulfilled') {
        setReportes(reportesRes.value.data);
        setReportesFiltrados(reportesRes.value.data);
      }

    } catch (error) {
      console.error('Error al cargar datos:', error);
      setNotificacion({ tipo: 'danger', mensaje: 'Error al cargar los datos' });
    } finally {
      setCargando(false);
    }
  };

  useEffect(() => {
    cargarDatos();
  }, [usuario]);

  // Función para filtrar reportes
  const filtrarReportes = () => {
    let filtrados = reportes;

    if (filtroFecha) {
      filtrados = filtrados.filter(reporte => 
        reporte.fecha && reporte.fecha.includes(filtroFecha)
      );
    }

    if (filtroGuardia) {
      filtrados = filtrados.filter(reporte => 
        reporte.nombre_guardia && 
        reporte.nombre_guardia.toLowerCase().includes(filtroGuardia.toLowerCase())
      );
    }

    setReportesFiltrados(filtrados);
  };

  useEffect(() => {
    filtrarReportes();
  }, [filtroFecha, filtroGuardia, reportes]);

  // Función para generar informe en PDF (simulado)
  const generarInformePDF = () => {
    const contenido = `
      INFORME DE INCIDENTES
      Fecha de generación: ${new Date().toLocaleDateString('es-ES')}
      
      Total de incidentes: ${reportesFiltrados.length}
      
      ${reportesFiltrados.map((reporte, index) => `
        Incidente #${index + 1}
        Guardia: ${reporte.nombre_guardia}
        Fecha: ${new Date(reporte.fecha).toLocaleDateString('es-ES')}
        Descripción: ${reporte.descripcion}
        ---
      `).join('')}
    `;

    // Crear blob y descargar
    const blob = new Blob([contenido], { type: 'text/plain' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `informe-incidentes-${new Date().toISOString().split('T')[0]}.txt`;
    document.body.appendChild(a);
    a.click();
    window.URL.revokeObjectURL(url);
    document.body.removeChild(a);

    setNotificacion({ tipo: 'success', mensaje: 'Informe descargado correctamente' });
  };

  // Función para exportar a Excel (simulado)
  const exportarAExcel = () => {
    const csvContent = [
      ['Guardia', 'Fecha', 'Descripción'],
      ...reportesFiltrados.map(reporte => [
        reporte.nombre_guardia,
        new Date(reporte.fecha).toLocaleDateString('es-ES'),
        reporte.descripcion
      ])
    ].map(row => row.join(',')).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `reportes-incidentes-${new Date().toISOString().split('T')[0]}.csv`;
    document.body.appendChild(a);
    a.click();
    window.URL.revokeObjectURL(url);
    document.body.removeChild(a);

    setNotificacion({ tipo: 'success', mensaje: 'Datos exportados a CSV correctamente' });
  };

  // Cerrar sesión
  const cerrarSesion = () => {
    localStorage.removeItem("usuario");
    window.location.reload();
  };

  // Limpiar notificación automática
  useEffect(() => {
    if (notificacion.mensaje) {
      const timer = setTimeout(() => setNotificacion({ tipo: '', mensaje: '' }), 3000);
      return () => clearTimeout(timer);
    }
  }, [notificacion]);

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center">
        <h3>Panel del Gerente - {usuario.nombre}</h3>
        <div>
          <button className="btn btn-info me-2" onClick={cargarDatos} disabled={cargando}>
            <i className="fas fa-sync-alt"></i> Actualizar
          </button>
          <button className="btn btn-danger" onClick={cerrarSesion}>Cerrar sesión</button>
        </div>
      </div>

      {/* Notificación */}
      {notificacion.mensaje && (
        <div className={`alert alert-${notificacion.tipo} mt-3`} role="alert">
          {notificacion.mensaje}
        </div>
      )}

      {/* Resumen de estadísticas */}
      <div className="row mt-4">
        <div className="col-md-3 mb-3">
          <div className="card bg-primary text-white">
            <div className="card-body text-center">
              <h4>{guardias.length}</h4>
              <p className="mb-0">👮‍♂️ Guardias</p>
            </div>
          </div>
        </div>
        <div className="col-md-3 mb-3">
          <div className="card bg-success text-white">
            <div className="card-body text-center">
              <h4>{turnos.length}</h4>
              <p className="mb-0">📅 Turnos</p>
            </div>
          </div>
        </div>
        <div className="col-md-3 mb-3">
          <div className="card bg-info text-white">
            <div className="card-body text-center">
              <h4>{tareas.length}</h4>
              <p className="mb-0">🛠 Tareas</p>
            </div>
          </div>
        </div>
        <div className="col-md-3 mb-3">
          <div className="card bg-warning text-dark">
            <div className="card-body text-center">
              <h4>{reportes.length}</h4>
              <p className="mb-0">📝 Incidentes</p>
            </div>
          </div>
        </div>
      </div>

      {/* Gestión de Locales */}
      <VistaLocales usuario={usuario} />

      {/* Informes de Incidentes */}
      <div className="card mt-4">
        <div className="card-header bg-warning text-dark">
          <h5 className="mb-0">📊 Informes de Incidentes</h5>
        </div>
        <div className="card-body">
          {/* Filtros */}
          <div className="row mb-3">
            <div className="col-md-4">
              <label className="form-label">Filtrar por fecha:</label>
              <input
                type="date"
                className="form-control"
                value={filtroFecha}
                onChange={e => setFiltroFecha(e.target.value)}
              />
            </div>
            <div className="col-md-4">
              <label className="form-label">Filtrar por guardia:</label>
              <input
                type="text"
                className="form-control"
                placeholder="Nombre del guardia"
                value={filtroGuardia}
                onChange={e => setFiltroGuardia(e.target.value)}
              />
            </div>
            <div className="col-md-4 d-flex align-items-end">
              <div className="btn-group w-100">
                <button className="btn btn-outline-primary" onClick={generarInformePDF}>
                  <i className="fas fa-file-pdf me-2"></i>Descargar PDF
                </button>
                <button className="btn btn-outline-success" onClick={exportarAExcel}>
                  <i className="fas fa-file-excel me-2"></i>Exportar CSV
                </button>
              </div>
            </div>
          </div>

          {/* Lista de reportes */}
          <div className="table-responsive">
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>#</th>
                  <th>Guardia</th>
                  <th>Fecha</th>
                  <th>Descripción</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {cargando ? (
                  <tr>
                    <td colSpan="5" className="text-center">
                      <div className="spinner-border text-warning" role="status">
                        <span className="visually-hidden">Cargando...</span>
                      </div>
                    </td>
                  </tr>
                ) : reportesFiltrados.length === 0 ? (
                  <tr>
                    <td colSpan="5" className="text-center text-muted">
                      No hay incidentes reportados
                    </td>
                  </tr>
                ) : (
                  reportesFiltrados.map((reporte, index) => (
                    <tr key={reporte._id}>
                      <td>{index + 1}</td>
                      <td>
                        <i className="fas fa-user me-2"></i>
                        {reporte.nombre_guardia}
                      </td>
                      <td>
                        <i className="fas fa-calendar me-2"></i>
                        {new Date(reporte.fecha).toLocaleDateString('es-ES')}
                      </td>
                      <td>
                        <span className="text-truncate d-inline-block" style={{maxWidth: '300px'}}>
                          {reporte.descripcion}
                        </span>
                      </td>
                      <td>
                        <button 
                          className="btn btn-sm btn-outline-info"
                          onClick={() => alert(`Descripción completa:\n\n${reporte.descripcion}`)}
                        >
                          <i className="fas fa-eye"></i>
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>

          {/* Resumen de filtros */}
          {reportesFiltrados.length > 0 && (
            <div className="mt-3">
              <small className="text-muted">
                Mostrando {reportesFiltrados.length} de {reportes.length} incidentes
                {filtroFecha && ` - Filtrado por fecha: ${filtroFecha}`}
                {filtroGuardia && ` - Filtrado por guardia: ${filtroGuardia}`}
              </small>
            </div>
          )}
        </div>
      </div>

      {/* Lista de Guardias */}
      <div className="card mt-4">
        <div className="card-header bg-primary text-white">
          <h5 className="mb-0">👮‍♂️ Guardias Registrados ({guardias.length})</h5>
        </div>
        <div className="card-body">
          {cargando ? (
            <div className="text-center">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
            </div>
          ) : guardias.length === 0 ? (
            <div className="text-center text-muted py-4">
              <i className="fas fa-users fa-3x mb-3"></i>
              <p>No hay guardias registrados</p>
            </div>
          ) : (
            <div className="row">
              {guardias.map((guardia, i) => (
                <div key={i} className="col-md-6 col-lg-4 mb-3">
                  <div className="card h-100 border-primary">
                    <div className="card-body">
                      <h6 className="card-title">
                        <i className="fas fa-user me-2"></i>
                        {guardia.nombre}
                      </h6>
                      <p className="card-text">
                        <strong>RUT:</strong> {guardia.rut}<br/>
                        <strong>Email:</strong> {guardia.email}<br/>
                        <strong>Rol:</strong> {guardia.rol}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Lista de Turnos */}
      <div className="card mt-4">
        <div className="card-header bg-success text-white">
          <h5 className="mb-0">📅 Turnos Asignados ({turnos.length})</h5>
        </div>
        <div className="card-body">
          {cargando ? (
            <div className="text-center">
              <div className="spinner-border text-success" role="status">
                <span className="visually-hidden">Cargando...</span>
              </div>
            </div>
          ) : turnos.length === 0 ? (
            <div className="text-center text-muted py-4">
              <i className="fas fa-calendar-alt fa-3x mb-3"></i>
              <p>No hay turnos asignados</p>
            </div>
          ) : (
            <div className="row">
              {turnos.map((turno, i) => (
                <div key={i} className="col-md-6 col-lg-4 mb-3">
                  <div className="card h-100 border-success">
                    <div className="card-header bg-success text-white">
                      <h6 className="mb-0">Turno #{i + 1}</h6>
                    </div>
                    <div className="card-body">
                      <div className="mb-2">
                        <strong>👤 Guardia:</strong> {turno.nombre_guardia}
                      </div>
                      <div className="mb-2">
                        <strong>🏢 Local:</strong> {turno.nombre_local || turno.local || turno.id_local}
                      </div>
                      <div className="mb-2">
                        <strong>📅 Fecha:</strong> {new Date(turno.fecha).toLocaleDateString('es-ES')}
                      </div>
                      <div className="mb-2">
                        <strong>⏰ Horario:</strong> {turno.hora_entrada} - {turno.hora_salida}
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

