import React, { useEffect, useState } from 'react';
import axios from 'axios';

function VistaSupervisor({ usuario }) {
  const [guardias, setGuardias] = useState([]);
  const [turnos, setTurnos] = useState([]);
  const [tarea, setTarea] = useState({ descripcion: '', guardia_id: '' });
  const [turnoSeleccionado, setTurnoSeleccionado] = useState(null);
  const [nuevoTurno, setNuevoTurno] = useState('');

  // Cargar guardias y turnos al inicio
  useEffect(() => {
    axios.get('http://localhost:5000/guardias', {
      headers: { rol: usuario.rol }
    }).then(res => setGuardias(res.data));

    axios.get('http://localhost:5000/turnos', {
      headers: { rol: usuario.rol }
    }).then(res => setTurnos(res.data));
  }, [usuario]);

  // Asignar tarea
  const asignarTarea = () => {
    axios.post('http://localhost:5000/tareas/asignar', tarea, {
      headers: { rol: usuario.rol }
    }).then(() => {
      alert('Tarea asignada correctamente');
      setTarea({ descripcion: '', guardia_id: '' });
    });
  };

  // Editar turno
  const cambiarTurno = () => {
    if (!turnoSeleccionado) return alert('Selecciona un turno');
    axios.put(`http://localhost:5000/turnos/editar/${turnoSeleccionado._id}`, {
      fecha: nuevoTurno
    }, {
      headers: { rol: usuario.rol }
    }).then(() => {
      alert('Turno actualizado');
      setNuevoTurno('');
    });
  };

  return (
    <div className="container mt-4">
      <h3>Panel del Supervisor - {usuario.nombre}</h3>

      {/* Asignación de tareas */}
      <div className="mt-5">
        <h5>Asignar Tarea</h5>
        <div className="form-group">
          <label>Descripción de la tarea</label>
          <input className="form-control" value={tarea.descripcion} onChange={e => setTarea({ ...tarea, descripcion: e.target.value })} />
        </div>
        <div className="form-group">
          <label>Seleccionar guardia</label>
          <select className="form-control" value={tarea.guardia_id} onChange={e => setTarea({ ...tarea, guardia_id: e.target.value })}>
            <option value="">-- Elegir --</option>
            {guardias.map(g => (
              <option key={g._id} value={g._id}>{g.nombre}</option>
            ))}
          </select>
        </div>
        <button className="btn btn-primary mt-2" onClick={asignarTarea}>Asignar</button>
      </div>

      {/* Modificar turnos */}
      <div className="mt-5">
        <h5>Cambiar Turno</h5>
        <div className="form-group">
          <label>Seleccionar turno</label>
          <select className="form-control" onChange={e => {
            const turno = turnos.find(t => t._id === e.target.value);
            setTurnoSeleccionado(turno);
          }}>
            <option value="">-- Elegir turno --</option>
            {turnos.map(t => (
              <option key={t._id} value={t._id}>
                {t.fecha} - {t.local} ({t.nombre_guardia})
              </option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label>Nuevo horario</label>
          <input className="form-control" type="datetime-local" value={nuevoTurno} onChange={e => setNuevoTurno(e.target.value)} />
        </div>
        <button className="btn btn-warning mt-2" onClick={cambiarTurno}>Actualizar Turno</button>
      </div>
    </div>
  );
}

export default VistaSupervisor;
