import React, { useEffect, useState } from 'react';
import axios from 'axios';

function VistaSupervisor() {
  const [tareas, setTareas] = useState([]);
  const [turnos, setTurnos] = useState([]);
  const [reportes, setReportes] = useState([]);

  useEffect(() => {
    // Obtener el usuario desde localStorage
    const usuario = JSON.parse(localStorage.getItem("usuario"));

    if (!usuario) {
      console.error("‚ö†Ô∏è No hay usuario en localStorage");
      return;
    }

    const headers = {
      'usuario': JSON.stringify({
        id: usuario.id,
        rol: usuario.rol
      })
    };

    // Obtener tareas
    axios.get('http://localhost:5000/tareas', { headers })
      .then(res => setTareas(res.data))
      .catch(err => console.error("‚ùå Error al obtener tareas:", err));

    // Obtener turnos
    axios.get('http://localhost:5000/turnos', { headers })
      .then(res => setTurnos(res.data))
      .catch(err => console.error("‚ùå Error al obtener turnos:", err));

    // Obtener reportes
    axios.get('http://localhost:5000/reportes', { headers })
      .then(res => setReportes(res.data))
      .catch(err => console.error("‚ùå Error al obtener reportes:", err));

  }, []);

  return (
    <div className="container mt-4">
      <h2>üìã Reportes Recibidos</h2>
      <ul className="list-group mb-4">
        {reportes.map((r, i) => (
          <li className="list-group-item" key={i}>
            <strong>Guardia:</strong> {r.nombre_guardia} <br />
            <strong>Reporte:</strong> {r.mensaje}
          </li>
        ))}
      </ul>

      <h2>üõ† Tareas Asignadas</h2>
      <ul className="list-group mb-4">
        {tareas.map((t, i) => (
          <li className="list-group-item" key={i}>
            <strong>Guardia:</strong> {t.nombre_guardia} <br />
            <strong>Tarea:</strong> {t.descripcion}
          </li>
        ))}
      </ul>

      <h2>üìÖ Turnos Generales</h2>
      <ul className="list-group">
        {turnos.map((t, i) => (
          <li className="list-group-item" key={i}>
            <strong>Guardia:</strong> {t.nombre_guardia} <br />
            <strong>Local:</strong> {t.local} <br />
            <strong>Hora:</strong> {t.hora}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default VistaSupervisor;
