import React, { useState } from 'react';
import axios from 'axios';

function Login({ setUsuario }) {
  const [correo, setCorreo] = useState('');
  const [contrasena, setContrasena] = useState('');

  const iniciarSesion = async () => {
    try {
      const res = await axios.post('http://localhost:5000/inicio-sesion', {
        correo, contrasena
      });
      setUsuario(res.data);
    } catch {
      alert('Credenciales incorrectas');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Iniciar sesión</h2>
      <div className="form-group">
        <label>Correo</label>
        <input className="form-control" value={correo} onChange={e => setCorreo(e.target.value)} />
      </div>
      <div className="form-group">
        <label>Contraseña</label>
        <input type="password" className="form-control" value={contrasena} onChange={e => setContrasena(e.target.value)} />
      </div>
      <button className="btn btn-primary mt-3" onClick={iniciarSesion}>Ingresar</button>
    </div>
  );
}

export default Login;
