import React, { useEffect, useState } from 'react';
import axios from 'axios';

function VistaSupervisor({ usuario }) {
  const [tareas, setTareas] = useState([]);
  const [turnos, setTurnos] = useState([]);
  const [notificacion, setNotificacion] = useState({ tipo: '', mensaje: '' });

  useEffect(() => {
    const headers = { Rol: usuario.rol };

    axios.get('http://localhost:5000/tareas', { headers })
      .then(res => {
        setTareas(res.data);
        setNotificacion({ tipo: 'success', mensaje: 'Tareas cargadas correctamente' });
      })
      .catch(err => {
        setNotificacion({ tipo: 'danger', mensaje: 'Error al cargar tareas' });
      });

    axios.get('http://localhost:5000/turnos', { headers })
      .then(res => {
        setTurnos(res.data);
        setNotificacion({ tipo: 'success', mensaje: 'Turnos cargados correctamente' });
      })
      .catch(err => {
        setNotificacion({ tipo: 'danger', mensaje: 'Error al cargar turnos' });
      });
  }, [usuario]);

  const cerrarSesion = () => {
    localStorage.removeItem("usuario");
    window.location.reload();
  };

  // Limpia la notificaciÃ³n despuÃ©s de unos segundos
  useEffect(() => {
    if (notificacion.mensaje) {
      const timer = setTimeout(() => {
        setNotificacion({ tipo: '', mensaje: '' });
      }, 2500);
      return () => clearTimeout(timer);
    }
  }, [notificacion]);

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center">
        <h3>Panel del Supervisor - {usuario.nombre}</h3>
        <button className="btn btn-danger" onClick={cerrarSesion}>Cerrar sesiÃ³n</button>
      </div>

      {/* Notificaciones */}
      {notificacion.mensaje && (
        <div className={`alert alert-${notificacion.tipo} mt-3`} role="alert">
          {notificacion.mensaje}
        </div>
      )}

      <h5 className="mt-4">ðŸ›  Tareas</h5>
      <ul className="list-group">
        {tareas.length > 0 ? (
          tareas.map((t, i) => (
            <li key={i} className="list-group-item">
              Guardia: {t.nombre_guardia} - Tarea: {t.descripcion}
            </li>
          ))
        ) : (
          <li className="list-group-item text-muted">No hay tareas asignadas</li>
        )}
      </ul>

      <h5 className="mt-4">ðŸ“… Turnos</h5>
      <ul className="list-group">
        {turnos.length > 0 ? (
          turnos.map((t, i) => (
            <li key={i} className="list-group-item">
              Guardia: {t.nombre_guardia} - Local: {t.local ? t.local : 'Sin datos'} - Fecha: {t.fecha}
            </li>
          ))
        ) : (
          <li className="list-group-item text-muted">No hay turnos asignados</li>
        )}
      </ul>
    </div>
  );
}

export default VistaSupervisor;
