import React, { useEffect, useState } from 'react';
import axios from 'axios';
import VistaLocales from './VistaLocales';

function VistaSupervisor({ usuario }) {
  // Listas y estado de la app
  const [tareas, setTareas] = useState([]);
  const [turnos, setTurnos] = useState([]);
  const [guardias, setGuardias] = useState([]);
  const [locales, setLocales] = useState([]);
  const [notificacion, setNotificacion] = useState({ tipo: '', mensaje: '' });

  // Estado para asignar turno (solo turno, sin tareas)
  const [formTurno, setFormTurno] = useState({
    id_guardia: '',
    id_local: '',
    fecha: '',
    hora_entrada: '',
    hora_salida: '',
  });

  // Estado para asignar tarea (solo tarea)
  const [formTarea, setFormTarea] = useState({
    id_guardia: '',
    id_local: '',
    descripcion: '',
    fecha: '', // Opcional si tu backend lo requiere
  });

  // Estado para múltiples tareas
  const [tareasMultiples, setTareasMultiples] = useState([]);
  const [formTareaMultiple, setFormTareaMultiple] = useState({
    id_guardia: '',
    id_local: '',
    descripcion: '',
    fecha: '',
  });

  // Cargar datos iniciales
  useEffect(() => {
    cargarDatos();
  }, [usuario]);

  // Función para cargar todos los datos
  const cargarDatos = async () => {
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    try {
      const [tareasRes, turnosRes, guardiasRes, localesRes] = await Promise.all([
        axios.get('http://localhost:5000/tareas', { headers }),
        axios.get('http://localhost:5000/turnos', { headers }),
        axios.get('http://localhost:5000/usuarios/guardias', { headers }),
        axios.get('http://localhost:5000/locales', { headers })
      ]);
      
      setTareas(tareasRes.data);
      setTurnos(turnosRes.data);
      setGuardias(guardiasRes.data);
      setLocales(localesRes.data);
    } catch (error) {
      console.error('Error al cargar datos:', error);
      setNotificacion({ tipo: 'danger', mensaje: 'Error al cargar los datos' });
    }
  };

  // Función para actualizar datos manualmente
  const actualizarDatos = async () => {
    setNotificacion({ tipo: 'info', mensaje: 'Actualizando datos...' });
    await cargarDatos();
    setNotificacion({ tipo: 'success', mensaje: 'Datos actualizados correctamente' });
  };

  // Función de prueba para verificar conexión con tareas
  const probarConexionTareas = () => {
    console.log('=== PRUEBA DE CONEXIÓN TAREAS ===');
    console.log('Usuario actual:', usuario);
    console.log('Rol del usuario:', usuario?.rol);
    console.log('ID del usuario:', usuario?.id);
    
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    console.log('Headers a enviar:', headers);
    
    // Probar GET de tareas
    axios.get('http://localhost:5000/tareas', { headers })
      .then(res => {
        console.log('✅ GET tareas exitoso:', res.data);
      })
      .catch(err => {
        console.error('❌ Error en GET tareas:', err);
      });
  };

  // Cerrar sesión
  const cerrarSesion = () => {
    localStorage.removeItem("usuario");
    window.location.reload();
  };

  // ----- ASIGNAR TURNO -----
  const handleChangeTurno = e => {
    setFormTurno({ ...formTurno, [e.target.name]: e.target.value });
  };

  const handleSubmitTurno = async e => {
    e.preventDefault();
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    try {
      await axios.post('http://localhost:5000/turnos/asignar', formTurno, { headers });
      setNotificacion({ tipo: 'success', mensaje: 'Turno asignado correctamente' });
      setFormTurno({ id_guardia: '', id_local: '', fecha: '', hora_entrada: '', hora_salida: '' });
      // Recargar datos
      await cargarDatos();
    } catch (error) {
      let msg = error.response?.data?.error || 'Error al asignar turno';
      setNotificacion({ tipo: 'danger', mensaje: msg });
    }
  };

  // ----- ASIGNAR TAREA -----
  const handleChangeTarea = e => {
    setFormTarea({ ...formTarea, [e.target.name]: e.target.value });
  };

  const handleSubmitTarea = async e => {
    e.preventDefault();
    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    
    console.log('=== DEBUG: Asignar tarea individual ===');
    console.log('Datos a enviar:', formTarea);
    console.log('Headers:', headers);
    
    try {
      await axios.post('http://localhost:5000/tareas/asignar', formTarea, { headers });
      setNotificacion({ tipo: 'success', mensaje: 'Tarea asignada correctamente' });
      setFormTarea({ id_guardia: '', id_local: '', descripcion: '', fecha: '' });
      // Recargar datos
      await cargarDatos();
    } catch (error) {
      console.error('Error al asignar tarea:', error);
      console.error('Response data:', error.response?.data);
      console.error('Response status:', error.response?.status);
      
      let msg = 'Error al asignar tarea';
      if (error.response?.data?.error) {
        msg = error.response.data.error;
      } else if (error.response?.data?.mensaje) {
        msg = error.response.data.mensaje;
      } else if (error.response?.status === 403) {
        msg = 'No tienes permisos para asignar tareas';
      } else if (error.response?.status === 400) {
        msg = 'Datos inválidos en la tarea';
      } else if (error.response?.status === 404) {
        msg = 'Guardia o local no encontrado';
      }
      
      setNotificacion({ tipo: 'danger', mensaje: msg });
    }
  };

  // ----- MÚLTIPLES TAREAS -----
  const handleChangeTareaMultiple = e => {
    setFormTareaMultiple({ ...formTareaMultiple, [e.target.name]: e.target.value });
  };

  const agregarTareaALista = () => {
    if (!formTareaMultiple.id_guardia || !formTareaMultiple.id_local || !formTareaMultiple.descripcion) {
      setNotificacion({ tipo: 'warning', mensaje: 'Por favor completa todos los campos obligatorios' });
      return;
    }

    const nuevaTarea = {
      ...formTareaMultiple,
      id: Date.now(), // ID temporal para identificar la tarea en la lista
    };

    setTareasMultiples([...tareasMultiples, nuevaTarea]);
    setFormTareaMultiple({ id_guardia: '', id_local: '', descripcion: '', fecha: '' });
    setNotificacion({ tipo: 'success', mensaje: 'Tarea agregada a la lista' });
  };

  const eliminarTareaDeLista = (id) => {
    setTareasMultiples(tareasMultiples.filter(tarea => tarea.id !== id));
  };

  const enviarTodasLasTareas = async () => {
    if (tareasMultiples.length === 0) {
      setNotificacion({ tipo: 'warning', mensaje: 'No hay tareas en la lista para enviar' });
      return;
    }

    const headers = { Rol: usuario.rol, Usuario_Id: usuario.id };
    let tareasExitosas = 0;
    let tareasFallidas = 0;
    let erroresDetallados = [];

    console.log('=== DEBUG: Enviar múltiples tareas ===');
    console.log('Total de tareas a enviar:', tareasMultiples.length);
    console.log('Headers:', headers);

    try {
      for (const tarea of tareasMultiples) {
        try {
          const { id, ...tareaSinId } = tarea; // Remover el ID temporal
          console.log('Enviando tarea:', tareaSinId);
          
          await axios.post('http://localhost:5000/tareas/asignar', tareaSinId, { headers });
          tareasExitosas++;
        } catch (error) {
          tareasFallidas++;
          console.error('Error al asignar tarea:', error);
          console.error('Response data:', error.response?.data);
          
          let errorMsg = 'Error desconocido';
          if (error.response?.data?.error) {
            errorMsg = error.response.data.error;
          } else if (error.response?.data?.mensaje) {
            errorMsg = error.response.data.mensaje;
          }
          
          erroresDetallados.push(`Tarea ${tareasFallidas}: ${errorMsg}`);
        }
      }

      if (tareasExitosas > 0) {
        let mensaje = `${tareasExitosas} tareas asignadas correctamente`;
        if (tareasFallidas > 0) {
          mensaje += `, ${tareasFallidas} fallaron`;
          if (erroresDetallados.length > 0) {
            console.error('Errores detallados:', erroresDetallados);
          }
        }
        
        setNotificacion({ tipo: 'success', mensaje });
        setTareasMultiples([]);
        // Recargar datos
        await cargarDatos();
      } else {
        setNotificacion({ 
          tipo: 'danger', 
          mensaje: `No se pudo asignar ninguna tarea. Errores: ${erroresDetallados.join(', ')}` 
        });
      }
    } catch (error) {
      console.error('Error general al procesar tareas:', error);
      setNotificacion({ tipo: 'danger', mensaje: 'Error al procesar las tareas' });
    }
  };

  const limpiarListaTareas = () => {
    setTareasMultiples([]);
    setNotificacion({ tipo: 'info', mensaje: 'Lista de tareas limpiada' });
  };

  // Limpiar notificación automática
  useEffect(() => {
    if (notificacion.mensaje) {
      const timer = setTimeout(() => setNotificacion({ tipo: '', mensaje: '' }), 3000);
      return () => clearTimeout(timer);
    }
  }, [notificacion]);

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center">
        <h3>Panel del Supervisor - {usuario.nombre}</h3>
        <div>
          <button className="btn btn-info me-2" onClick={actualizarDatos}>
            <i className="fas fa-sync-alt"></i> Actualizar Datos
          </button>
          <button className="btn btn-outline-info me-2" onClick={probarConexionTareas}>
            🔍 Probar Tareas
          </button>
          <button className="btn btn-danger" onClick={cerrarSesion}>Cerrar sesión</button>
        </div>
      </div>

      {/* Notificación */}
      {notificacion.mensaje && (
        <div className={`alert alert-${notificacion.tipo} mt-3`} role="alert">
          {notificacion.mensaje}
        </div>
      )}

      {/* FORMULARIO PARA ASIGNAR TURNO */}
      <div className="card mt-4">
        <div className="card-body">
          <h5>Asignar Turno</h5>
          <form onSubmit={handleSubmitTurno}>
            <div className="row g-2">
              <div className="col-md-3">
                <label className="form-label">Guardia</label>
                <select
                  className="form-select"
                  name="id_guardia"
                  value={formTurno.id_guardia}
                  onChange={handleChangeTurno}
                  required
                >
                  <option value="">Selecciona</option>
                  {guardias.map(g => (
                    <option key={g._id} value={g._id}>{g.nombre}</option>
                  ))}
                </select>
              </div>
              <div className="col-md-3">
                <label className="form-label">Local</label>
                <select
                  className="form-select"
                  name="id_local"
                  value={formTurno.id_local}
                  onChange={handleChangeTurno}
                  required
                >
                  <option value="">Selecciona</option>
                  {locales.map(l => (
                    <option key={l._id} value={l._id}>{l.nombre}</option>
                  ))}
                </select>
              </div>
              <div className="col-md-2">
                <label className="form-label">Fecha</label>
                <input
                  type="date"
                  className="form-control"
                  name="fecha"
                  value={formTurno.fecha}
                  onChange={handleChangeTurno}
                  required
                />
              </div>
              <div className="col-md-2">
                <label className="form-label">Hora Entrada</label>
                <input
                  type="time"
                  className="form-control"
                  name="hora_entrada"
                  value={formTurno.hora_entrada}
                  onChange={handleChangeTurno}
                  required
                />
              </div>
              <div className="col-md-2">
                <label className="form-label">Hora Salida</label>
                <input
                  type="time"
                  className="form-control"
                  name="hora_salida"
                  value={formTurno.hora_salida}
                  onChange={handleChangeTurno}
                  required
                />
              </div>
            </div>
            <button type="submit" className="btn btn-success mt-2">Asignar Turno</button>
          </form>
        </div>
      </div>

      {/* FORMULARIO PARA ASIGNAR TAREA INDIVIDUAL */}
      <div className="card mt-4">
        <div className="card-body">
          <h5>Asignar Tarea Individual</h5>
          <form onSubmit={handleSubmitTarea}>
            <div className="row g-2">
              <div className="col-md-3">
                <label className="form-label">Guardia</label>
                <select
                  className="form-select"
                  name="id_guardia"
                  value={formTarea.id_guardia}
                  onChange={handleChangeTarea}
                  required
                >
                  <option value="">Selecciona</option>
                  {guardias.map(g => (
                    <option key={g._id} value={g._id}>{g.nombre}</option>
                  ))}
                </select>
              </div>
              <div className="col-md-3">
                <label className="form-label">Local</label>
                <select
                  className="form-select"
                  name="id_local"
                  value={formTarea.id_local}
                  onChange={handleChangeTarea}
                  required
                >
                  <option value="">Selecciona</option>
                  {locales.map(l => (
                    <option key={l._id} value={l._id}>{l.nombre}</option>
                  ))}
                </select>
              </div>
              <div className="col-md-4">
                <label className="form-label">Descripción de la tarea</label>
                <input
                  type="text"
                  className="form-control"
                  name="descripcion"
                  value={formTarea.descripcion}
                  onChange={handleChangeTarea}
                  required
                />
              </div>
              <div className="col-md-2">
                <label className="form-label">Fecha</label>
                <input
                  type="date"
                  className="form-control"
                  name="fecha"
                  value={formTarea.fecha}
                  onChange={handleChangeTarea}
                />
              </div>
            </div>
            <button type="submit" className="btn btn-primary mt-2">Asignar Tarea</button>
          </form>
        </div>
      </div>

      {/* FORMULARIO PARA MÚLTIPLES TAREAS */}
      <div className="card mt-4">
        <div className="card-body">
          <h5>Asignar Múltiples Tareas</h5>
          <div className="row g-2">
            <div className="col-md-3">
              <label className="form-label">Guardia</label>
              <select
                className="form-select"
                name="id_guardia"
                value={formTareaMultiple.id_guardia}
                onChange={handleChangeTareaMultiple}
                required
              >
                <option value="">Selecciona</option>
                {guardias.map(g => (
                  <option key={g._id} value={g._id}>{g.nombre}</option>
                ))}
              </select>
            </div>
            <div className="col-md-3">
              <label className="form-label">Local</label>
              <select
                className="form-select"
                name="id_local"
                value={formTareaMultiple.id_local}
                onChange={handleChangeTareaMultiple}
                required
              >
                <option value="">Selecciona</option>
                {locales.map(l => (
                  <option key={l._id} value={l._id}>{l.nombre}</option>
                ))}
              </select>
            </div>
            <div className="col-md-4">
              <label className="form-label">Descripción de la tarea</label>
              <input
                type="text"
                className="form-control"
                name="descripcion"
                value={formTareaMultiple.descripcion}
                onChange={handleChangeTareaMultiple}
                required
              />
            </div>
            <div className="col-md-2">
              <label className="form-label">Fecha</label>
              <input
                type="date"
                className="form-control"
                name="fecha"
                value={formTareaMultiple.fecha}
                onChange={handleChangeTareaMultiple}
              />
            </div>
          </div>
          <div className="mt-2">
            <button 
              type="button" 
              className="btn btn-info me-2" 
              onClick={agregarTareaALista}
            >
              ➕ Agregar a Lista
            </button>
            <button 
              type="button" 
              className="btn btn-success me-2" 
              onClick={enviarTodasLasTareas}
              disabled={tareasMultiples.length === 0}
            >
              📤 Enviar Todas las Tareas ({tareasMultiples.length})
            </button>
            <button 
              type="button" 
              className="btn btn-warning" 
              onClick={limpiarListaTareas}
              disabled={tareasMultiples.length === 0}
            >
              🗑️ Limpiar Lista
            </button>
          </div>

          {/* Lista de tareas pendientes */}
          {tareasMultiples.length > 0 && (
            <div className="mt-3">
              <h6>Tareas en lista ({tareasMultiples.length}):</h6>
              <div className="list-group">
                {tareasMultiples.map((tarea, index) => {
                  const guardia = guardias.find(g => g._id === tarea.id_guardia);
                  const local = locales.find(l => l._id === tarea.id_local);
                  return (
                    <div key={tarea.id} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <strong>{index + 1}.</strong> {guardia?.nombre} - {local?.nombre} - {tarea.descripcion}
                        {tarea.fecha && <span className="text-muted ms-2">({tarea.fecha})</span>}
                      </div>
                      <button 
                        className="btn btn-sm btn-outline-danger" 
                        onClick={() => eliminarTareaDeLista(tarea.id)}
                      >
                        ❌
                      </button>
                    </div>
                  );
                })}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Gestión de Locales */}
      <VistaLocales usuario={usuario} />

      {/* Lista de tareas */}
      <div className="card mt-4">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h5 className="mb-0">🛠 Tareas Asignadas ({tareas.length})</h5>
          <button className="btn btn-sm btn-outline-primary" onClick={actualizarDatos}>
            <i className="fas fa-sync-alt"></i> Actualizar
          </button>
        </div>
        <div className="card-body">
          {tareas.length > 0 ? (
            <div className="row">
              {tareas.map((tarea, i) => (
                <div key={i} className="col-md-6 col-lg-4 mb-3">
                  <div className="card h-100 border-primary">
                    <div className="card-header bg-primary text-white">
                      <h6 className="mb-0">Tarea #{i + 1}</h6>
                    </div>
                    <div className="card-body">
                      <div className="mb-2">
                        <strong>👤 Guardia:</strong> {tarea.nombre_guardia}
                      </div>
                      <div className="mb-2">
                        <strong>🏢 Local:</strong> {tarea.nombre_local}
                      </div>
                      <div className="mb-2">
                        <strong>📝 Descripción:</strong> {tarea.descripcion}
                      </div>
                      {tarea.fecha && (
                        <div className="mb-2">
                          <strong>📅 Fecha:</strong> {new Date(tarea.fecha).toLocaleDateString('es-ES')}
                        </div>
                      )}
                      {tarea.estado && (
                        <div className="mb-2">
                          <strong>Estado:</strong> 
                          <span className={`badge ms-2 ${
                            tarea.estado === 'completada' ? 'bg-success' : 
                            tarea.estado === 'en_progreso' ? 'bg-warning' : 'bg-secondary'
                          }`}>
                            {tarea.estado}
                          </span>
                        </div>
                      )}
                      {tarea.fecha_creacion && (
                        <div className="text-muted small">
                          <strong>Creada:</strong> {new Date(tarea.fecha_creacion).toLocaleString('es-ES')}
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center text-muted py-4">
              <i className="fas fa-tasks fa-3x mb-3"></i>
              <p>No hay tareas asignadas</p>
            </div>
          )}
        </div>
      </div>

      {/* Lista de turnos */}
      <div className="card mt-4">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h5 className="mb-0">📅 Turnos Asignados ({turnos.length})</h5>
          <button className="btn btn-sm btn-outline-success" onClick={actualizarDatos}>
            <i className="fas fa-sync-alt"></i> Actualizar
          </button>
        </div>
        <div className="card-body">
          {turnos.length > 0 ? (
            <div className="row">
              {turnos.map((turno, i) => (
                <div key={i} className="col-md-6 col-lg-4 mb-3">
                  <div className="card h-100 border-success">
                    <div className="card-header bg-success text-white">
                      <h6 className="mb-0">Turno #{i + 1}</h6>
                    </div>
                    <div className="card-body">
                      <div className="mb-2">
                        <strong>👤 Guardia:</strong> {turno.nombre_guardia}
                      </div>
                      <div className="mb-2">
                        <strong>🏢 Local:</strong> {turno.nombre_local || turno.local || turno.id_local}
                      </div>
                      <div className="mb-2">
                        <strong>📅 Fecha:</strong> {new Date(turno.fecha).toLocaleDateString('es-ES')}
                      </div>
                      <div className="mb-2">
                        <strong>⏰ Horario:</strong>
                        <div className="ms-3">
                          <div>🟢 Entrada: {turno.hora_entrada}</div>
                          <div>🔴 Salida: {turno.hora_salida}</div>
                        </div>
                      </div>
                      {turno.duracion && (
                        <div className="mb-2">
                          <strong>⏱️ Duración:</strong> {turno.duracion} horas
                        </div>
                      )}
                      {turno.estado && (
                        <div className="mb-2">
                          <strong>Estado:</strong> 
                          <span className={`badge ms-2 ${
                            turno.estado === 'activo' ? 'bg-success' : 
                            turno.estado === 'pendiente' ? 'bg-warning' : 
                            turno.estado === 'completado' ? 'bg-info' : 'bg-secondary'
                          }`}>
                            {turno.estado}
                          </span>
                        </div>
                      )}
                      {turno.fecha_creacion && (
                        <div className="text-muted small">
                          <strong>Creado:</strong> {new Date(turno.fecha_creacion).toLocaleString('es-ES')}
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center text-muted py-4">
              <i className="fas fa-calendar-alt fa-3x mb-3"></i>
              <p>No hay turnos asignados</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default VistaSupervisor;
